C<pre><code>#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;

int main(int argc, char* argv[]) {
    int x;//要读的数字 
    int digit;//数字的每一位上的数 
    int count = 0;//计数器，用于数给定的字的位数 
    int ret = 0;//原数字的逆序结果，比如原数字是123,ret就是321 
    int i;//这是for循环里要用到的一个变量，先定义出来

    scanf("%d", &amp;x); //用户输入一个数字

    //如果用户输入的是一个负数，则先打印fu和它后面的空格
    //并将该数变为一个正整数 
    if (x &lt; 0) {
        printf("fu ");
        x = -x;
    }

    //用对10取余的方法将数字逆转，并记录下它是一个几位数 
    while (x &gt; 0) {
        digit = x % 10;
        x /= 10;
        ret = ret * 10 + digit;
        count++;
    }

    //因为该数字至少有一位，而且要考虑最后一个数字后面没有空格的问题
    //所以我们先把该数字的个位“脱”下来，并打印 
    digit = ret % 10;
    ret /= 10;
    //switch语句能够让程序根据数字来找到合适的拼音并打印 
    switch (digit) {
    case 0:
        printf("ling");
        break;
    case 1:
        printf("yi");
        break;
    case 2:
        printf("er");
        break;
    case 3:
        printf("san");
        break;
    case 4:
        printf("si");
        break;
    case 5:
        printf("wu");
        break;
    case 6:
        printf("liu");
        break;
    case 7:
        printf("qi");
        break;
    case 8:
        printf("ba");
        break;
    case 9:
        printf("jiu");
        break;
    }

    //因为我们刚才已经把个位数打印了，所以for循环的起点是1
    //循环原数字位数-1次 
    for (i = 1; i &lt; count; i++) {
        digit = ret % 10;
        ret /= 10;
        //这是每两个数字之间的空格 
        printf(" ");
        switch (digit) {
        case 0:
            printf("ling");
            break;
        case 1:
            printf("yi");
            break;
        case 2:
            printf("er");
            break;
        case 3:
            printf("san");
            break;
        case 4:
            printf("si");
            break;
        case 5:
            printf("wu");
            break;
        case 6:
            printf("liu");
            break;
        case 7:
            printf("qi");
            break;
        case 8:
            printf("ba");
            break;
        case 9:
            printf("jiu");
            break;
        }
    }
    return 0;
}
</code></pre>

C<pre><code>#include &lt;stdio.h&gt;

int main() {
    int nums[10] = { 0 };
    int maxCount = 0;

    for (int i = 0; i &lt; 5; i++) {
        int num;
        scanf("%d", &amp;num);

        while (num != 0) {
            int digit = num % 10;
            nums[digit]++;
            num /= 10;
        }
    }

    for (int i = 0; i &lt; 10; i++) {
        if (nums[i] &gt; maxCount) {
            maxCount = nums[i];
        }
    }

    printf("出现%d次数字有：", maxCount);
    for (int i = 0; i &lt; 10; i++) {
        if (nums[i] == maxCount) {
            printf("%d ", i);
        }
    }
    printf("\n");

    return 0;
}
