C<pre><code>#include &lt;stdio.h&gt;
#define N 6

void conv(int a[N]) {
    int temp[6];
    for (int i = 0; i &lt; 6; i++)
    {
        for (int j = 5; j &gt;= 0;)
        {
            temp[i++] = a[j--];
        }
    }
    for (int i = 0; i &lt; 6; i++)
    {
        a[i] = temp[i];
    }
}

int main() {
    int num[N] = { 11,9,8,2,1,0 };
    conv(num);
    for (int i = 0; i &lt; 6; i++)
    {
        printf("%d ", num[i]);
    }
    return 0;
}
