C<pre><code>#include &lt;stdio.h&gt;

// 函数声明
void insertAndSort(int arr[], int size, int num);

int main() {
    int numbers[5] = { 2, 12, 18, 22, 33 };
    int num;

    // 输入待插入的整数
    scanf("%d", &amp;num);

    // 插入并排序
    insertAndSort(numbers, 5, num);

    // 输出结果
    for (int i = 0; i &lt; 5 + 1; i++) {
        printf("%d ", numbers[i]);
    }

    return 0;
}

// 插入并排序函数
void insertAndSort(int arr[], int size, int num) {
    int i = size - 1;

    // 找到插入位置
    while (i &gt;= 0 &amp;&amp; arr[i] &gt; num) {
        arr[i + 1] = arr[i];
        i--;
    }

    // 插入数字
    arr[i + 1] = num;
}
