C<pre><code>#include &lt;stdio.h&gt;

// 定义数组的大小
#define SIZE 10

// 函数声明
void work(int arr[], int size);

int main() {
    int balls[SIZE];

    // 输入球的标号
    for (int i = 0; i &lt; SIZE; i++) {
        scanf("%d", &amp;balls[i]);
    }

    // 冒泡排序
    work(balls, SIZE);

    // 输出排序结果
    printf("排序结果：");
    for (int i = 0; i &lt; SIZE; i++) {
        printf("%d ", balls[i]);
    }

    return 0;
}

// 冒泡排序函数
void work(int arr[], int size) {
    for (int i = 1; i &lt; size; i++) {
        for (int j = 1; j &lt; size - i + 1; j++) {
            // 降序排序，如果前面的元素比后面的元素小，交换它们
            if (arr[j - 1] &lt; arr[j]) {
                // 交换两个元素的位置
                int temp = arr[j - 1];
                arr[j - 1] = arr[j];
                arr[j] = temp;
            }
        }
    }
}
</code></pre>

