C<pre><code>#include &lt;stdio.h&gt;
#include &lt;ctype.h&gt;

int main() {
    char text[3][81];
    int upperCaseCount = 0;
    int lowerCaseCount = 0;
    int digitCount = 0;
    int spaceCount = 0;
    int otherCount = 0;

    for (int i = 0; i &lt; 3; i++) {
        fgets(text[i], 81, stdin);
    }

    for (int i = 0; i &lt; 3; i++) {
        for (int j = 0; text[i][j] != '\0'; j++) {
            if (isupper(text[i][j])) {
                upperCaseCount++;
            }
            else if (islower(text[i][j])) {
                lowerCaseCount++;
            }
            else if (isdigit(text[i][j])) {
                digitCount++;
            }
            else if (text[i][j] == ' ') {
                spaceCount++;
            }
            else if (text[i][j] != '\n' &amp;&amp; text[i][j] != '\r') {
                otherCount++;
            }
        }
    }

    printf("upper case: %d\n", upperCaseCount);
    printf("lower case: %d\n", lowerCaseCount);
    printf("digit     : %d\n", digitCount);
    printf("space     : %d\n", spaceCount);
    printf("other     : %d\n", otherCount);

    return 0;
}
</code></pre>

