C<pre><code>#include &lt;stdio.h&gt;

int main() {
    int length, arr[20], number, i;

    // 输入数组长度
    scanf("%d", &amp;length);

    // 输入数组数据
    for (i = 0; i &lt; length; i++)
        scanf("%d", &amp;arr[i]);

    // 输入需要插入的数据
    scanf("%d", &amp;number);

    // 找到插入位置
    int insertIndex = 0;
    while (insertIndex &lt; length &amp;&amp; arr[insertIndex] &lt; number) {
        insertIndex++;
    }

    // 插入数据
    for (i = length; i &gt; insertIndex; i--) {
        arr[i] = arr[i - 1];
    }
    arr[insertIndex] = number;

    // 输出完成插入的数组
    for (i = 0; i &lt; length + 1; i++) {
        printf("%5d", arr[i]);
    }
    printf("\n");

    return 0;
}
</code></pre>

