C<pre><code>#include &lt;stdio.h&gt;

void reverse(int src[], int dst[], int total)
{
    // 请编写程序，将数组 src 中的元素逆序存放到 dst 中
    for (int i = 0; i &lt; total; i++)
    {
        dst[i] = src[total - i - 1];
    }

}

int main() {
    int total;
    int src[30];
    scanf("%d", &amp;total);

    for (int i = 0; i &lt; total; i++) {
        scanf("%d", &amp;src[i]);
    }

    int dst[30];
    reverse(src, dst, total);
    for (int i = 0; i &lt; total; i++) {
        printf("%d ", dst[i]);
    }

    return 0;
