C<pre><code>#include &lt;stdio.h&gt;

void decimalToBase(int decimal, int base) {
    char digits[36] = "0123456789abcdefghijklmnopqrstuvwxyz";
    char result[100];
    int index = 0;

    if (decimal == 0) {
        printf("0\n");
        return;
    }

    while (decimal &gt; 0) {
        int remainder = decimal % base;
        result[index++] = digits[remainder];
        decimal /= base;
    }

    for (int i = index - 1; i &gt;= 0; i--) {
        printf("%c", result[i]);
    }
    printf("\n");
}

int main() {
    int decimal, base;

    printf("请输入一个十进制正整数：\n");
    scanf("%d", &amp;decimal);
    printf("请输入目标进制（大于1小于37）：\n");
    scanf("%d", &amp;base);

    printf("转换结果为：\n");
    decimalToBase(decimal, base);

    return 0;
}
