C<pre><code>#include &lt;stdio.h&gt;
#include &lt;string.h&gt;

void work(char str[]) {
    int len = strlen(str);
    int start = 1;

    // 颠倒整个句子
    for (int i = 0; i &lt; len / 2; i++) {
        char temp = str[i];
        str[i] = str[len - i - 1];
        str[len - i - 1] = temp;
    }

    // 颠倒每个单词
    for (int i = 0; i &lt;= len; i++) {
        if (str[i] == ' ' || str[i] == '\0') {
            for (int j = start, k = i - 1; j &lt; k; j++, k--) {
                char temp = str[j];
                str[j] = str[k];
                str[k] = temp;
            }
            start = i + 1;
        }
    }

    // 输出颠倒后的句子
    printf("%s\n", str);
}

int main() {
    char str[100];

    // 输入一句英语
    fgets(str, sizeof(str), stdin);

    // 输出颠倒后的所有单词
    work(str);

    return 0;
}
