C<pre><code>#include &lt;stdio.h&gt;
#define N 10

void Input(int b[N]) {
    //输入函数
    int i;
    printf("请输入%d个整数：\n",N);
    for(i=0; i&lt;N; i++) {
        scanf("%d",&amp;b[i]);
    }
}

void BubbleSort(int b[N]) {
    //冒泡排序函数
    int i,j,t;
    for(i=0; i&lt;N-1; i++) {
        //外层循环控制趟数
        for(j=0; j&lt;N-1-i; j++) {
            //内层循环控制每趟的比较次数
            if(b[j]&gt;b[j+1]) {
                t=b[j];
                b[j]=b[j+1];
                b[j+1]=t;
            }
        }
    }
}

void Print(int b[N]) {
    //输出函数
    int i;
    for(i=0; i&lt;N; i++) {
        if(i%10==0)
            printf("\n");
        printf("%6d",b[i]);
    }
    printf("\n");
}

int main() {
    int a[N];
    Input(a);
    printf("排序前：");
    Print(a);
    BubbleSort (a);
    printf("排序后：");
    Print(a);
    return 0;
}
