    char inputString[100];

    // 输入字符串
    fgets(inputString, sizeof(inputString), stdin);

    // 调用函数反序存放字符串
    inverse(inputString);

    // 输出结果
    printf(" %s\n", inputString);

    return 0;
}

// 反序存放字符串的函数
void inverse(char str[]) {
    int length = strlen(str);

    // 使用两个指针进行反序交换
    int start = 0;
    int end = length - 1;

    while (start &lt; end) {
        // 交换字符
        char temp = str[start];
        str[start] = str[end];
        str[end] = temp;

        // 移动指针
        start++;
        end--;
    }
}
