        p++;
        i++;
    }
    if (*p == '\0') {
        return;
    }
    else
        while (*p != '\0') {
            p++;
            i++;
        }
    while (*q != '\0') {
        q++;
        x++;
    }
    for (j = i; j &gt;= pos; j--) {
        *(p + x) = *p;
        p--;
    }
    q--;

    for (j = 1; j &lt;= x; j++) {
        *(p + x) = *q;
        p--;
        q--;
    }
}
