C<pre><code>void Next(char* T, int* next) {
    int i = 1;
    next[1] = 0;
    int j = 0;
    while (i &lt; strlen(T)) {
        if (j == 0 || T[i - 1] == T[j - 1]) {
            i++;
            j++;
            next[i] = j;
        }
        else {
            j = next[j];
        }
    }
}

int KMP(char* S, char* T) {
    int next[10];
    Next(T, next);
    int i = 1;
    int j = 1;
    while (i &lt;= strlen(S) &amp;&amp; j &lt;= strlen(T)) {
        if (j == 0 || S[i - 1] == T[j - 1]) {
            i++;
            j++;
        }
        else {
            j = next[j];
        }
    }
    if (j &gt; strlen(T)) {
        return i - (int)strlen(T);
    }
    return -1;
}
