C<pre><code>void insert(char* s, char* t, int pos) {
    int s_len = strlen(s);
    int t_len = strlen(t);
    int new_len = s_len + t_len;
    int i, j;

    pos--; // 设置首元素下标为‘1’

    // 向右移动原字符串s中的字符，给插入的字符串t腾出空间
    for (i = s_len; i &gt;= pos; i--) {
        s[i + t_len] = s[i];
    }

    // 将字符串t插入到字符串s中
    for (j = 0; j &lt; t_len; j++) {
        s[pos + j] = t[j];
    }

    // 输出插入后的新字符串
    for (i = 0; i &lt; new_len; i++) {
        printf("%c", s[i]);
    }
}
