C<pre><code>void insert(char* s, char* t, int pos) {
    //将字符串插入到另一个字符串的指定位置

    char* strs = s, * strt = t;

    int i = 0;
    while (*strs &amp;&amp; i &lt; pos - 1) {
        strs++;
        i++;
    }
    if (pos &lt; 1 || *strs == '\0') {
        printf("输入的pos值错误！\n");
        exit(0);
    }//判断是否输入错误，输入错误时，正常退出 

    while (*strt != '\0') {
        strt++;//找到t字符串的末位 
    }
    strs = s + pos - 1;
    while (*strs != '\0') {
        *(strt++) = *(strs++);//将字符串s的pos后的字符放到t中 
    }
    strs = s + pos - 1;
    while (*t != '\0') {
        *(strs++) = *(t++);//pos后的部分放回原位置 
    }
    *strt = *strs = '\0';//末尾制空
    printf("%s", s);//输出字符串s 
}
