C<pre><code>void Change(int A[], int n) {
    //将所有正数都排在负数前面
    int i = 0, j = n - 1, temp;
    while (i &lt; j) {
        if (i &lt; j &amp;&amp; A[i]&gt;0)
            i++;
        if (i &lt; j &amp;&amp; A[j] &lt; 0)
            j--;
        if (i &lt; j) {
            temp = A[i];
            A[i++] = A[j];
            A[j--] = temp;
        }
    }
}
