C<pre><code>void Find(int arr[][COL], int row, int col) {
    int i, j;
    printf("请输入数组：\n");

    for (i = 0; i &lt; row; i++) {
        for (j = 0; j &lt; col; j++) {
            scanf("%d", &amp;arr[i][j]);
        }
    }

    int tag = 0;
    for (i = 0; i &lt; row; i++) {
        int row_max = arr[i][0]; // 初始化每行的最大值
        int col_index = 0; // 记录最大值所在的列索引

        // 找出当前行的最大值和其列索引
        for (j = 1; j &lt; col; j++) {
            if (arr[i][j] &gt; row_max) {
                row_max = arr[i][j];
                col_index = j;
            }
        }

        // 检查当前列是否为该最大值所在的列中的最小值
        int col_min = arr[0][col_index];
        for (j = 0; j &lt; row; j++) {
            if (arr[j][col_index] &lt; col_min) {
                col_min = arr[j][col_index];
            }
        }

        // 如果该列中的最小值与该行中的最大值相等，则找到鞍点
        if (col_min == row_max) {
            printf("该数组中存在鞍点 arr[%d][%d] = %d\n", i, col_index, row_max);
            tag = 1;
        }
    }

    if (tag == 0) {
        printf("该数组中不存在鞍点！\n");
    }
}
