        printf("%d ", p-&gt;data);
    }
}

void SelectSort(LinkList* L) {
    //简单选择排序
    LinkList left, right, p = (*L)-&gt;next;
    int temp;

    while (p) {
        right = p-&gt;next;
        left = p;

        while (right) {
            if (right-&gt;data &lt; left-&gt;data) {
                left = right;
            } 
            right = right-&gt;next;
        }

        if (left != p) {
            temp = left-&gt;data;
            left-&gt;data = p-&gt;data;
            p-&gt;data = temp;
        }
        p = p-&gt;next;
    }
}
