C<pre><code>int search(int a[], int n, int key)  
{
    int low = 0;
    int high = n-1;
    int mid;
    while (low &lt;= high)
    {
        mid = (low+high)/2;
        if (key == a[mid])
            return mid;
        else if (key &gt; a[mid])
            low = mid+1;
        else
            high = mid-1;
    }
    return -1; 
