C<pre><code>int Ack(int m, int n) {
    //Ack函数 ，递归算法 
    if (m == 0)
        return n + 1;
    else if (m != 0 &amp;&amp; n == 0)
        return Ack(m - 1, 1);
    else if (m != 0 &amp;&amp; n != 0)
        return Ack(m - 1, Ack(m, n - 1));
}
