C<pre><code>int Ack_re(int m, int n) {
    //Ack函数 ， 非递归
    int akm[M][N];
    int i, j;
    for (j = 0; j &lt; N; j++)
        akm[0][j] = j + 1;
    for (i = 1; i &lt; M; i++) {
        akm[i][0] = akm[i - 1][1];
        for (j = 1; j &lt; N; j++)
            akm[i][j] = akm[i - 1][akm[i][j - 1]];
    }
    return (akm[m][n]);
}
