C<pre><code>void Create_LinkList(LinkList* L, int n) {
    // 创建链表
    *L = (LinkList)malloc(sizeof(LNode)); // 创建头结点
    (*L)-&gt;next = NULL;
    LinkList rear; // 尾指针
    rear = *L;     // 指向头结点
    for (int i = 0; i &lt; n; i++) {
        LinkList p = (LinkList)malloc(sizeof(LNode)); // 给新结点创建空间
        p-&gt;next = NULL;
        printf("单链表中第%2d个元素是：", i + 1);   // 下标是从0开始的，得加1
        scanf("%d", &amp;p-&gt;data);
        rear-&gt;next = p; // 新结点插入链表尾部
        rear = p;       // rear指向新的尾节点
    }
}

void print_LinkList(LinkList L) {
    // 输出链表
    LinkList p = L;
    while (p-&gt;next != NULL) {
        p = p-&gt;next; // 跳过头结点输出下一个结点中存储的数值
        printf("%d", p-&gt;data);
    }
}

int Sub_Sequence(LinkList La, LinkList Lb) {
    // 判断字符串B是否是字符串A的子串

    LinkList pa = La, pb = Lb, first = pa; // first记录每一次的子串首元素
    while (pa &amp;&amp; pb) {
        if (pa-&gt;data == pb-&gt;data) {
            pa = pa-&gt;next;
            pb = pb-&gt;next;
        }
        else {
            first = first-&gt;next; // 下一个first验证
            pa = first;          // pa从first遍历
            pb = Lb;             // pb从开始遍历
        }
    }
    if (pb == NULL) // A未结束，B结束了
        return 1;    // YES
    else             // A完了，B没完
        return 0;    // NO
}
