C<pre><code>int merge(LinkList A, LinkList B, LinkList C)
{
    LinkList pA = A-&gt;next;
    LinkList pB = B-&gt;next;
    LinkList pC = C;
    pC-&gt;next = NULL;

    LinkList temp = (LinkList)malloc(sizeof(LNode));
    temp-&gt;next = NULL;

    while (pA &amp;&amp; pB) {
        if (pA-&gt;data &lt; pB-&gt;data) {
            temp = pA-&gt;next;
            pA-&gt;next = pC -&gt; next;
            pC-&gt;next = pA;
            pA = temp;
        }
        else if (pA-&gt;data &gt; pB-&gt;data) {
            temp = pB-&gt;next;
            pB-&gt;next = pC-&gt;next;
            pC-&gt;next = pB;
            pB = temp;
        }
        else {
            temp = pA-&gt;next;
            pA-&gt;next = pC-&gt;next;
            pC-&gt;next = pA;
            pA = temp;
            pB = pB-&gt;next;
