void Split_List(LinkList* La, LinkList* Lb, LinkList* Lc) {
    LinkList pA = (*La)-&gt;next;
    LinkList pB = NULL;
    LinkList pC = NULL;

    *Lb = (LNode*)malloc(sizeof(LNode));
    (*Lb)-&gt;next = NULL;
    pB = *Lb;

    *Lc = (LNode*)malloc(sizeof(LNode));
    (*Lc)-&gt;next = NULL;
    pC = *Lc;

    while (pA != NULL) {
        if (pA-&gt;data &lt; 0) {
            LinkList newNode = (LinkList)malloc(sizeof(LNode));
            newNode-&gt;data = pA-&gt;data;
            newNode-&gt;next = NULL;
            pB-&gt;next = newNode;
            pB = pB-&gt;next;
        }
        else {
            LinkList newNode = (LinkList)malloc(sizeof(LNode));
            newNode-&gt;data = pA-&gt;data;
            newNode-&gt;next = NULL;
            pC-&gt;next = newNode;
            pC = pC-&gt;next;
        }
        pA = pA-&gt;next;
    }

    // 逆转链表B
    pB = *Lb;
    LinkList prev = NULL;
    LinkList curr = pB-&gt;next;
    LinkList next;
    while (curr) {
        next = curr-&gt;next;
        curr-&gt;next = prev;
        prev = curr;
        curr = next;
    }
    pB-&gt;next = prev;

    // 逆转链表C
    pC = *Lc;
    prev = NULL;
    curr = pC-&gt;next;
    while (curr) {
        next = curr-&gt;next;
        curr-&gt;next = prev;
        prev = curr;
        curr = next;
    }
    pC-&gt;next = prev;
}
