void Split_List(LinkList* La, LinkList* Lb, LinkList* Lc) {
    // 将A链表分解成B、C两个链表

    LinkList pa = (*La)-&gt;next;
    *Lb = (LinkList)malloc(sizeof(LNode));
    *Lc = (LinkList)malloc(sizeof(LNode));
    (*Lc)-&gt;next = NULL;
