            pa-&gt;next = (*Lb)-&gt;next;
            (*Lb)-&gt;next = pa;
        }
        else if (pa-&gt;data &gt; 0) {  // 把大于0的数放入C中
            pa-&gt;next = (*Lc)-&gt;next;
            (*Lc)-&gt;next = pa;
        }
        pa = next;  // 指向下一个待处理的点
    }
}
