C<pre><code>#define _CRT_SECURE_NO_WARNINGS 1

#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
#include &lt;windows.h&gt;

typedef struct Student {
    char name[50];
    char ID[20] = { 0 };
    float score[4];
    float sum;
    struct Student* next;
} Student;

// 全局变量，指向链表头节点
Student* head = NULL;

int count = 0; // 统计数量

// 插入学生信息
void insertStudent() {
    Student* newStudent = (Student*)malloc(sizeof(Student));
    if (newStudent == NULL) {
        printf("内存分配失败！\a\n");
        exit(1);
    }

    newStudent-&gt;sum = 0.0;

    printf("请输入学生姓名: ");
    scanf("%s", newStudent-&gt;name);

    printf("请输入学生学号: ");
    scanf("%s", newStudent-&gt;ID);

    printf("请依次输入学生四门课程成绩: ");

    for (int i = 0; i &lt; 4; i++) {
        scanf("%f", &amp;(newStudent-&gt;score[i]));
        newStudent-&gt;sum += newStudent-&gt;score[i];
    }
    newStudent-&gt;next = NULL;

    // 如果链表为空，将新节点设置为头节点
    if (head == NULL) {
        head = newStudent;
    }
    else {
        // 否则，遍历链表找到最后一个节点，并将新节点插入到最后
        Student* current = head;
        while (current-&gt;next != NULL) {
            current = current-&gt;next;
        }
        current-&gt;next = newStudent;
    }

    printf("学生信息插入成功！\n");
    count++;
}

// 删除学生信息
void deleteStudent() {
    if (head == NULL) {
        printf("链表为空，无法删除学生信息！\a\n");
        return;
    }

    char studentID[20] = { 0 };
    printf("请输入要删除的学生学号: ");
    scanf("%s", studentID);

    Student* current = head;
    Student* prev = NULL;

    // 遍历链表查找要删除的学生节点
    while (current != NULL &amp;&amp; strcmp(current-&gt;ID, studentID) != 0) {
        prev = current;
        current = current-&gt;next;
    }

    // 如果找到了要删除的学生节点
    if (current != NULL) {
        // 如果要删除的学生节点是头节点
        if (prev == NULL) {
            head = current-&gt;next;
        }
        else {
            prev-&gt;next = current-&gt;next;
        }
        free(current);
        free(prev);

        printf("学生信息删除成功！\n");
        count--;
    }
    else {
        printf("未找到学号为%s的学生信息！\a\n", studentID);
    }
}

// 查询学生信息
void queryStudent() {
    if (head == NULL) {
        printf("链表为空，无学生信息！\a\n");
        return;
    }

    char studentID[20] = { 0 };
    printf("请输入要查询的学生学号: ");
    scanf("%s", studentID);

    Student* current = head;

    // 遍历链表查找要查询的学生节点
    while (current != NULL &amp;&amp; strcmp(current-&gt;ID, studentID) != 0) {
        current = current-&gt;next;
    }

    // 如果找到了要查询的学生节点
    if (current != NULL) {
        printf("\n");
        printf("学生姓名: %s\n", current-&gt;name);
        printf("学生学号: %s\n", current-&gt;ID);
        printf("学生成绩: ");

        for (int i = 0; i &lt; 4; i++) {
            printf("%.2f ", current-&gt;score[i]);
        }
        printf("\n");

        printf("学生总成绩: %.2f\n", current-&gt;sum);
    }
    else {
        printf("未找到学号为%s的学生信息！\a\n", studentID);
    }
}

// 排名学生信息
void rankStudents() {
    if (head == NULL) {
        printf("链表为空，无学生信息！\a\n");
        return;
    }

    // 创建一个临时数组，用于存储学生信息
    int length = 0;
    Student* current = head;
    while (current != NULL) {
        length++;
        current = current-&gt;next;
    }

    Student** students = (Student**)malloc(sizeof(Student*) * length);
    if (students == NULL) {
        printf("内存分配失败，程序意外退出！\a\n");
        exit(1);
    }

    current = head;
    for (int i = 0; i &lt; length; i++) {
        students[i] = current;
        current = current-&gt;next;
    }

    // 使用冒泡排序对学生信息按总成绩进行排序
    for (int i = 0; i &lt; length - 1; i++) {
        for (int j = 0; j &lt; length - 1 - i; j++) {
            if (students[j]-&gt;sum &lt; students[j + 1]-&gt;sum) {
                Student* temp = students[j];
                students[j] = students[j + 1];
                students[j + 1] = temp;
            }
        }
    }


    printf("学生成绩排名如下：\n");
    for (int i = 0; i &lt; length; i++) {
        printf("\n");
        printf("排名 %d:\n", i + 1);
        printf("姓名: %s\n", students[i]-&gt;name);
        printf("学号: %s\n", students[i]-&gt;ID);
        printf("成绩:\n");
        for (int j = 0; j &lt; 4; j++) {
            printf("    科目 %d:  %.2f\n", j + 1, students[i]-&gt;score[j]);
        }
        printf("总成绩: %.2f\n", students[i]-&gt;sum);
        printf("--------------------\n");
    }

    free(students);
}

