        fprintf(file, "Score: ");
        for (int i = 0; i &lt; 4; i++) {
            fprintf(file, "%.2f ", current-&gt;score[i]);
        }
        fprintf(file, "\n");
        fprintf(file, "Sum: %.2f\n", current-&gt;sum);
        fprintf(file, "\n");
        current = current-&gt;next;
    }

    fclose(file);
    printf("学生信息已成功导出到文件%s！\n", filename);
