// 由文件导入学生信息
void readFromFile() {
    char filename[50] = { 0 };
    printf("请输入要导入的文件名: ");
    scanf("%s", filename);

    FILE* file = fopen(filename, "r");
    if (file == NULL) {
        printf("无法打开文件！\a\n");
        return;
    }

    Student* current = NULL;
    Student* previous = NULL;

    while (1) {
        Student* newStudent = (Student*)malloc(sizeof(Student));
        if (newStudent == NULL) {
            printf("内存分配失败，程序意外退出！\a\n");
            exit(1);
        }


        if (fscanf(file, "Name: %s\n", newStudent-&gt;name) != 1)
            break;

        count++;

        fscanf(file, "ID: %s\n", newStudent-&gt;ID);
        fscanf(file, "Score: ");
        for (int i = 0; i &lt; 4; i++) {
            fscanf(file, "%f", &amp;newStudent-&gt;score[i]);
        }
        fscanf(file, "\n");
        fscanf(file, "Sum: %f\n", &amp;newStudent-&gt;sum);
        fscanf(file, "\n");
        // 注意看，这里的fsanf和导出数据函数中的fprintf并不是完全相同的。


        newStudent-&gt;next = NULL;

        if (current == NULL) {
            current = newStudent;
            head = newStudent;
        }
        else {
            previous-&gt;next = newStudent;
            current = newStudent;
        }
        previous = newStudent;
    }

    fclose(file);
    printf("学生信息已成功从文件%s导入！\n", filename);
}

