C<pre><code>/*
Name:   LinkList.cpp
Author: 侯霖钰
data: 24/03/24 15:23
Description: 单链组的一系列操作
*/


#define _CRT_SECURE_NO_WARNINGS 1

#include &lt;iostream&gt;
#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;

typedef int ElemType;
int count = 0; // 计数

// 定义单链表结点结构体
typedef struct SListNode {
    ElemType data;
    struct SListNode* next;
} LNode, * LinkList;


// 初始化单链表
void InitList(LinkList* List) {
    *List = NULL;  // 初始时链表为空，头指针为NULL
}


// 尾插法插入结点
void SListPushBack(LinkList* pphead, ElemType x) {
    LinkList newnode = (LinkList)malloc(sizeof(LNode));
    newnode-&gt;data = x;
    newnode-&gt;next = NULL;

    if (*pphead == NULL) {
        *pphead = newnode;
    }
    else {
        LinkList tail = *pphead;
        while (tail-&gt;next != NULL) {
            tail = tail-&gt;next;
        }
        tail-&gt;next = newnode;
    }

    count++;
}


// 头插法插入结点
void SListPushFront(LinkList* pphead, ElemType x) {
    LinkList newnode = (LinkList)malloc(sizeof(LNode));
    newnode-&gt;data = x;
    newnode-&gt;next = *pphead;
    *pphead = newnode;

    count++;
}


// 头删操作
void SListPopFront(LinkList* pphead) {
    if (*pphead == NULL) {
        printf("当前为空链表！\a\n");
        return;
    }
    LinkList next = (*pphead)-&gt;next;
    free(*pphead);
    *pphead = next;

    printf("删除成功！\n");
    count--;
}


// 尾删操作
void SListPopBack(LinkList* pphead) {
    if (*pphead == NULL) {
        printf("当前为空链表！\a\n");
        return;
    }
    else if ((*pphead)-&gt;next == NULL) {
        free(*pphead);
        *pphead = NULL;
    }
    else {
        LinkList prev = NULL;
        LinkList tail = *pphead;
        while (tail-&gt;next != NULL) {
            prev = tail;
            tail = tail-&gt;next;
        }
        free(tail);
        prev-&gt;next = NULL;
    }

    printf("删除成功！\n");
    count--;
}


// 打印单链表
void SListPrint(LinkList phead) {
    LinkList cur = phead;
    int index = 1;
    while (cur != NULL) {
        printf("(第%d位):%d --&gt;", index, cur-&gt;data);
        cur = cur-&gt;next;
        index++;
    }
    printf("NULL\n");
}


// 在index的前面插入x
void SListInsert(LinkList* phead, int index, ElemType x) {
    if (*phead == NULL) {
        printf("\n当前链表为空，默认插入首位！\n");
        SListPushFront(phead, x);
        return;
    }

    index--; // 因为链表的首元素的索引值设置为‘1’

    LinkList cur = *phead;
    int num = 1;
    while (cur != NULL &amp;&amp; num &lt; index) {
        cur = cur-&gt;next;
        num++;
    }

    if (cur == NULL) {
        printf("未找到指定位置\n");
        return;
    }

    LinkList newnode = (LinkList)malloc(sizeof(LNode));
    if (newnode == NULL) {
        printf("内存分配失败\a\n");
        return;
    }

    newnode-&gt;data = x;
    newnode-&gt;next = cur-&gt;next;
    cur-&gt;next = newnode;

    count++;
}


// 删除索引位置的结点
void SListErase(LinkList* phead, int index) {
    if (*phead == NULL) {
        return;
    }

    index--; // 因为设置单链表首元素的索引值为‘1’

    if (index == 0) { // 删除的元素为首元素，其实直接调用头删就行
        LinkList temp = *phead;
        *phead = (*phead)-&gt;next;
        free(temp);
        count--;
        return;
    }

    LinkList prev = *phead;
    int currIndex = 1;
    while (prev != NULL &amp;&amp; currIndex &lt; index) {
        prev = prev-&gt;next;
        currIndex++;
    }

    if (prev == NULL || prev-&gt;next == NULL) {
        printf("未找到指定结点\n");
        return;
    }

    LinkList pos = prev-&gt;next;
    prev-&gt;next = pos-&gt;next;
    free(pos);
    count--;
}


// 在链表中查找值为x的结点
int SListFind(LinkList phead, ElemType x) {
    LinkList cur = phead;
    int index = 0;
    while (cur != NULL) {
        if (cur-&gt;data == x) {
            return index;
        }
        cur = cur-&gt;next;
        index++;
    }

    return -1;
}


// 由索引值查找元素
void FindFromIndex(LinkList L, int index) {
    if (L == NULL || index &lt;= 0) {
        printf("不存在第 %d 位元素\n", index);
        return;
    }

    LinkList p = L;
    int i = 0;
    index--; // 因为第一个元素的索引值设置为了‘1’

    while (p &amp;&amp; i &lt; index) {
        p = p-&gt;next;
        i++;
    }

    if (i == index &amp;&amp; p) {
        //printf("第%d位元素是：%d\n", index, p-&gt;data);
        std::cout &lt;&lt; "第" &lt;&lt; index + 1 &lt;&lt; "位元素是：" &lt;&lt; p-&gt;data &lt;&lt; std::endl;
    }
    else {
        printf("未查询到第%d位元素\n", index);
    }
}


// 检查是否为“非降序”,
bool isNotDecreasing(LinkList list) {
    if (list == NULL || list-&gt;next == NULL) {
        return true;  // 空链表或只有一个节点的链表视为非降序
    }

    LinkList cur = list;
    while (cur-&gt;next != NULL) {
        if (cur-&gt;data &gt; cur-&gt;next-&gt;data) {
            return false;  // 出现降序，返回false
        }
        cur = cur-&gt;next;
    }

    return true;  // 遍历完链表没有出现降序，返回true
}


// 合并两个链表
LinkList Combine(LinkList La, LinkList Lb) {
    if (La == NULL) {
        return Lb;
    }
    if (Lb == NULL) {
        return La;
    }

    LinkList head = NULL;
    LinkList tail = NULL;

    LinkList curA = La;
    LinkList curB = Lb;

    if (curA-&gt;data &lt;= curB-&gt;data) {
        head = tail = curA;
        curA = curA-&gt;next;
    }
    else {
        head = tail = curB;
        curB = curB-&gt;next;
    }

    while (curA != NULL &amp;&amp; curB != NULL) {
        if (curA-&gt;data &lt;= curB-&gt;data) {
            tail-&gt;next = curA;
            curA = curA-&gt;next;
        }
        else {
            tail-&gt;next = curB;
            curB = curB-&gt;next;
        }
        tail = tail-&gt;next;
    }

    // 将剩余未遍历完的链表连接到结果链表的末尾
    if (curA != NULL) {
        tail-&gt;next = curA;
    }
    else if (curB != NULL) {
        tail-&gt;next = curB;
    }

    return head;
}


// 清空单链表
void ClearSList(LinkList* phead) {
    *phead = NULL;
    count = 0;
}


// 销毁单链表
void DestroyList(LinkList* phead) {
    LinkList cur = *phead;
    while (cur != NULL) {
        LinkList next = cur-&gt;next;
        free(cur);
        cur = next;
    }
    *phead = NULL;
}


