C<pre><code>#define _CRT_SECURE_NO_WARNINGS 1

#include &lt;stdio.h&gt;
#include &lt;stdbool.h&gt;
#include &lt;assert.h&gt;
#include &lt;stdlib.h&gt;

typedef int STDataType;

typedef struct Stack
{
    STDataType* a;
    int top; // 栈顶（相当于是下标）
    int capacity; // 容量
}ST;

void StackInit(ST* ps);
void StackDestory(ST* ps);

// 入栈
void StackPush(ST* ps, STDataType x);

// 出栈
void StackPop(ST* ps);

STDataType StackTop(ST* ps);
int StackSize(ST* ps);
bool StackEmpty(ST* ps);



void StackInit(ST* ps)
{
    assert(ps);

    ps-&gt;a = (STDataType*)malloc(sizeof(STDataType) * 4);
    if (ps-&gt;a == NULL)
    {
        printf("malloc fail\n");
        exit(-1);
    }

    ps-&gt;capacity = 4;
    ps-&gt;top = 0;
}

void StackDestory(ST* ps)
{
    assert(ps);
    free(ps-&gt;a);
    ps-&gt;a = NULL;
    ps-&gt;top = ps-&gt;capacity = 0;
}

// 入栈
void StackPush(ST* ps, STDataType x)
{
    assert(ps);

    // 满了-&gt;增容
    if (ps-&gt;top == ps-&gt;capacity)
    {
        STDataType* tmp = (STDataType*)realloc(ps-&gt;a, ps-&gt;capacity * 2 * sizeof(STDataType));
        if (tmp == NULL)
        {
            printf("realloc fail\n");
            exit(-1);
        }
        else
        {
            ps-&gt;a = tmp;
            ps-&gt;capacity *= 2;
        }
    }

    ps-&gt;a[ps-&gt;top] = x;
    ps-&gt;top++;
}

// 出栈
void StackPop(ST* ps)
{
    assert(ps);
    // 栈空了，调用Pop，直接中止程序报错
    assert(ps-&gt;top &gt; 0);

    //ps-&gt;a[ps-&gt;top - 1] = 0;
    ps-&gt;top--;
}

STDataType StackTop(ST* ps)
{
    assert(ps);
    // 栈空了，调用Top，直接中止程序报错
    assert(ps-&gt;top &gt; 0);

    return ps-&gt;a[ps-&gt;top - 1];
}

int StackSize(ST* ps)
{
    assert(ps);

    return ps-&gt;top;
}

bool StackEmpty(ST* ps)
{
    assert(ps);

    return ps-&gt;top == 0;
}


int main()
{
    ST st;
    StackInit(&amp;st);
    StackPush(&amp;st, 1);
    StackPush(&amp;st, 2);
    StackPush(&amp;st, 3);

    printf("%d ", StackTop(&amp;st));
    StackPop(&amp;st);
    printf("%d ", StackTop(&amp;st));
    StackPop(&amp;st);

    StackPush(&amp;st, 4);
    StackPush(&amp;st, 5);

    while (!StackEmpty(&amp;st))
    {
        printf("%d ", StackTop(&amp;st));
        StackPop(&amp;st);
    }
    printf("\n");

    StackDestory(&amp;st);

    return 0;
}
</code></pre>

C<pre><code>#define _CRT_SECURE_NO_WARNINGS 1

#include &lt;stdio.h&gt;
#include &lt;stdbool.h&gt;
#include &lt;assert.h&gt;
#include &lt;stdlib.h&gt;

typedef int QDataType;

typedef struct QueueNode
{
    struct QueueNode* next;
    QDataType data;
}QNode;

typedef struct Queue
{
    QNode* head;
    QNode* tail;
}Queue;


void QueueInit(Queue* pq);
void QueueDestory(Queue* pq);

// 队尾入
void QueuePush(Queue* pq, QDataType x);

// 队头出
void QueuePop(Queue* pq);

QDataType QueueFront(Queue* pq);
QDataType QueueBack(Queue* pq);

int QueueSize(Queue* pq);
bool QueueEmpty(Queue* pq);



// 功能实现

void QueueInit(Queue* pq)
{
    assert(pq);
    pq-&gt;head = pq-&gt;tail = NULL;
}

void QueueDestory(Queue* pq)
{
    assert(pq);

    QNode* cur = pq-&gt;head;
    while (cur)
    {
        QNode* next = cur-&gt;next;
        free(cur);
        cur = next;
    }

    pq-&gt;head = pq-&gt;tail = NULL; // 别忘了这个
}

// 队尾入
void QueuePush(Queue* pq, QDataType x)
{
    assert(pq);
    QNode* newnode = (QNode*)malloc(sizeof(QNode));
    if (newnode == NULL)
    {
        printf("malloc fail\n");
        exit(-1);
    }

    newnode-&gt;data = x;
    newnode-&gt;next = NULL;

    if (pq-&gt;tail == NULL)
    {
        pq-&gt;head = pq-&gt;tail = newnode;
    }
    else
    {
        pq-&gt;tail-&gt;next = newnode;
        pq-&gt;tail = newnode;
    }
}

// 队头出
void QueuePop(Queue* pq)
{
    assert(pq);
    assert(pq-&gt;head);



    if (pq-&gt;head-&gt;next == NULL) // 队列中只有一个元素
    {
        free(pq-&gt;head);
        pq-&gt;head = pq-&gt;tail = NULL;
    }
    else
    {
        QNode* next = pq-&gt;head-&gt;next;
        free(pq-&gt;head);
        pq-&gt;head = next;
    }
}


QDataType QueueFront(Queue* pq)
{
    assert(pq);
    assert(pq-&gt;head);

    return pq-&gt;head-&gt;data;
}

QDataType QueueBack(Queue* pq)
{
    assert(pq);
    assert(pq-&gt;head);

    return pq-&gt;tail-&gt;data;
}

int QueueSize(Queue* pq)
{
    assert(pq);
    int size = 0;
    QNode* cur = pq-&gt;head;
    while (cur)
    {
        ++size;
        cur = cur-&gt;next;
    }

    return size;
}

bool QueueEmpty(Queue* pq)
{
    assert(pq);
    return pq-&gt;head == NULL;
}


int main()
{
    Queue q;
    QueueInit(&amp;q);
    QueuePush(&amp;q, 1);
    QueuePush(&amp;q, 2);
    printf("%d ", QueueFront(&amp;q));
    QueuePop(&amp;q);
    printf("%d ", QueueFront(&amp;q));
    QueuePop(&amp;q);

    QueuePush(&amp;q, 3);
    QueuePush(&amp;q, 4);

    while (!QueueEmpty(&amp;q))
    {
        printf("%d ", QueueFront(&amp;q));
        QueuePop(&amp;q);
    }
    printf("\n");

    QueueDestory(&amp;q);

    return 0;
}
</code></pre>

