PHP<pre><code>&lt;?php
function change($str)
{
    return str_replace("x", "xx", $str);
}

$name = $_GET['name'];
$age = "I am 11";
$arr = array($name, $age);
echo "反序列化字符串：";
var_dump(serialize($arr));
echo "&lt;br/&gt;";
echo "过滤后:";
